import{_ as s,v as e,b as a,R as t}from"./chunks/framework.70afa331.js";const y=JSON.parse('{"title":"仓库","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"case/case1.md","filePath":"case/case1.md","lastUpdated":1733408504000}'),o={name:"case/case1.md"},p=t('<h1 id="仓库" tabindex="-1">仓库 <a class="header-anchor" href="#仓库" aria-label="Permalink to &quot;仓库&quot;">​</a></h1><p><a href="https://github.com/spiritLHLS/ecs" target="_blank" rel="noreferrer">https://github.com/spiritLHLS/ecs</a></p><p><a href="https://www.spiritlhl.net/" target="_blank" rel="noreferrer"><img src="https://hits.seeyoufarm.com/api/count/incr/badge.svg?url=https%3A%2F%2Fgithub.com%2FspiritLHLS%2Fecs&amp;count_bg=%2357DEFF&amp;title_bg=%23000000&amp;icon=cliqz.svg&amp;icon_color=%23E7E7E7&amp;title=hits&amp;edge_flat=false" alt="Hits"></a></p><h1 id="融合怪测评脚本" tabindex="-1">融合怪测评脚本 <a class="header-anchor" href="#融合怪测评脚本" aria-label="Permalink to &quot;融合怪测评脚本&quot;">​</a></h1><p><strong>如果有本项目未列出的系统/架构，或本项目测试有BUG测不出来，或测试不想要魔改本机配置想要最小化环境变动的</strong></p><p><strong>请尝试<a href="https://github.com/oneclickvirt/ecs" target="_blank" rel="noreferrer">https://github.com/oneclickvirt/ecs</a>进行测试</strong></p><p>支持系统：</p><p>Ubuntu 18+, Debian 8+, Centos 7+, Fedora 33+, Almalinux 8.5+, OracleLinux 8+, RockyLinux 8+, AstraLinux CE, Arch</p><p>半支持系统：</p><p>FreeBSD(前提已执行<code>pkg install -y curl bash</code>)，Armbian</p><p>支持架构：</p><p>amd64(x86_64)、arm64、i386、arm</p><p>支持地域：</p><p>能连得上网都支持</p><h2 id="部分服务器运行测试有各类bug一键修复后再测试" tabindex="-1">部分服务器运行测试有各类bug一键修复后再测试 <a class="header-anchor" href="#部分服务器运行测试有各类bug一键修复后再测试" aria-label="Permalink to &quot;部分服务器运行测试有各类bug一键修复后再测试&quot;">​</a></h2><p>一键修复各种系统原生bug的仓库：</p><p><a href="https://github.com/spiritLHLS/one-click-installation-script" target="_blank" rel="noreferrer">https://github.com/spiritLHLS/one-click-installation-script</a></p><p>如若还有系统bug请到上面仓库的issues反映，脚本原生BUG该仓库issues反映</p><h2 id="融合怪命令" tabindex="-1">融合怪命令 <a class="header-anchor" href="#融合怪命令" aria-label="Permalink to &quot;融合怪命令&quot;">​</a></h2><h3 id="交互形式" tabindex="-1">交互形式 <a class="header-anchor" href="#交互形式" aria-label="Permalink to &quot;交互形式&quot;">​</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-L</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://gitlab.com/spiritysdx/za/-/raw/main/ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span></span></code></pre></div><p>或</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-L</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://github.com/spiritLHLS/ecs/raw/main/ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span></span></code></pre></div><p>或</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">bash &lt;(wget -qO- bash.spiritlhl.net/ecs)</span></span></code></pre></div><p>或</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">bash &lt;(wget -qO- ecs.0s.hk)</span></span></code></pre></div><p>或</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">bash &lt;(wget -qO- ecs.12345.ing)</span></span></code></pre></div><h3 id="无交互形式-参数模式" tabindex="-1">无交互形式-参数模式 <a class="header-anchor" href="#无交互形式-参数模式" aria-label="Permalink to &quot;无交互形式-参数模式&quot;">​</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-L</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://gitlab.com/spiritysdx/za/-/raw/main/ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-m</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span></span></code></pre></div><p>或</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-L</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://github.com/spiritLHLS/ecs/raw/main/ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-m</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span></span></code></pre></div><p>或通过</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">curl -L https://gitlab.com/spiritysdx/za/-/raw/main/ecs.sh -o ecs.sh &amp;&amp; chmod +x ecs.sh</span></span></code></pre></div><p>下载文件后使用类似</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-m</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span></span></code></pre></div><p>这样的参数命令指定选项执行</p><p>以下为参数说明：</p><table><thead><tr><th>指令</th><th>项目</th><th>说明</th><th>备注</th></tr></thead><tbody><tr><td>-m</td><td>必填项</td><td>可指定原本menu中的对应选项，最多支持三层选择，例如执行<code>bash ecs.sh -m 5 1 1</code>将选择主菜单第5选项下的第1选项下的子选项1的脚本执行</td><td>可缺省仅指定一个参数，如<code>-m 1</code>仅指定执行融合怪完全体，执行<code>-m 1 0</code>以及<code>-m 1 0 0</code>都是指定执行融合怪完全体</td></tr><tr><td>-en</td><td>可选项</td><td>可指定强制输出为英文</td><td>无该指令则默认使用中文输出</td></tr><tr><td>-i</td><td>可选项</td><td>可指定回程路由测试中的目标IPV4地址</td><td>可通过<code>ip.sb</code>、<code>ipinfo.io</code>等网站获取本地IPV4地址后指定</td></tr><tr><td>-r</td><td>可选项</td><td>可指定回程路由测试中的目标IPV4地址，可选<code>b</code> <code>g</code> <code>s</code> <code>c</code> 分别对应<code>北京</code>、<code>广州</code>、<code>上海、</code>成都```</td><td>如<code>-r b</code>指定测试北京回程(三网)</td></tr><tr><td></td><td></td><td>可指定仅测试IPV6三网，可选 <code>b6</code> <code>g6</code> <code>s6</code> 分别对应 <code>北京</code>、<code>广州</code>、<code>上海</code> 的三网的IPV6地址</td><td>如<code>-r b6</code> 指定测试北京IPV6地址回程(三网)</td></tr><tr><td>-base</td><td>可选项</td><td>可指定仅测试基础的系统信息</td><td>无该指令则默认按照menu选项的组合测试</td></tr><tr><td>-ctype</td><td>可选项</td><td>可指定通过何种方式测试cpu，可选<code>gb4</code>、<code>gb5</code>、<code>gb6</code>分别对应<code>geekbench</code>的<code>4</code>、<code>5</code>、<code>6</code>版本</td><td>无该指令则默认使用<code>sysbench</code>测试</td></tr><tr><td>-dtype</td><td>可选项</td><td>可指定测试硬盘IO的程序，可选<code>dd</code>、<code>fio</code>，前者测试快后者测试慢</td><td>无该指令则默认都使用进行测试</td></tr><tr><td>-mdisk</td><td>可选项</td><td>可指定测试多个挂载盘的IO</td><td>注意本指令包含测试系统盘</td></tr><tr><td>-stype</td><td>可选项</td><td>可指定使用<code>.cn</code>还是<code>.net</code>的数据进行测速</td><td>无该指令则默认使用<code>.net</code>数据测速优先，不可用时才替换为<code>.cn</code>数据</td></tr><tr><td>-bansp</td><td>可选项</td><td>可指定强制不测试网速</td><td>无该指令则默认测试网速</td></tr><tr><td>-banup</td><td>可选项</td><td>可指定强制不生成分享链接</td><td>无该指令则默认生成分享链接</td></tr></tbody></table><h2 id="ip质量检测" tabindex="-1">IP质量检测 <a class="header-anchor" href="#ip质量检测" aria-label="Permalink to &quot;IP质量检测&quot;">​</a></h2><ul><li>IP质量检测，含多家数据库查询，含DNS黑名单查询</li><li>含 <code>IPV4</code> 和 <code>IPV6</code> 检测，含ASN和地址查询</li><li>含邮件常用协议/端口检测，如果SMTP/SMTPS可用且本地端口开放，则可搭建邮局</li></ul><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&lt;(</span><span style="color:#FFCB6B;">wget</span><span style="color:#C3E88D;"> -qO- bash.spiritlhl.net/ecs-ipcheck</span><span style="color:#89DDFF;">)</span></span></code></pre></div><p>或</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&lt;(</span><span style="color:#FFCB6B;">wget</span><span style="color:#C3E88D;"> -qO- --no-check-certificate https://raw.githubusercontent.com/spiritLHLS/ecs/main/ipcheck.sh</span><span style="color:#89DDFF;">)</span></span></code></pre></div><p>或</p><p>需要事先安裝<code>dos2unix</code></p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">wget</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-qO</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ipcheck.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">--no-check-certificate</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://gitlab.com/spiritysdx/za/-/raw/main/ipcheck.sh</span></span>\n<span class="line"><span style="color:#FFCB6B;">dos2unix</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ipcheck.sh</span></span>\n<span class="line"><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ipcheck.sh</span></span></code></pre></div><h2 id="融合怪说明" tabindex="-1">融合怪说明 <a class="header-anchor" href="#融合怪说明" aria-label="Permalink to &quot;融合怪说明&quot;">​</a></h2><p>融合怪脚本最好在 /root 路径下执行，避免各种奇奇怪怪的问题</p><p>融合怪的执行结果保存在当前路径下的<code>test_result.txt</code>中，可在<code>screen</code>或<code>tmux</code>中执行，先退出SSH登录过一段时间后再查看文件</p><p><strong>有时候想要测一些配置极其拉跨的机器时，上面这样执行这样可以避免IO或者CPU过于垃圾导致的测试过程中的SSH连接中断，就不会测一半啥都没了，假如screen中显示乱码，也没问题，分享链接中的结果是不带乱码的</strong></p><p>融合怪的完整版和精简版运行完毕会自动上传结果到pastebin并回传分享链接，如果测一半想要退出，那么按<code>Ctrl+C</code>同时按下可终止测试，此时会自动退出删除残余文件</p><p>最烂机器测试的例子(跑了47分钟一样测完)：<a href="https://github.com/spiritLHLS/ecs/blob/main/lowpage/README.md" target="_blank" rel="noreferrer">跳转</a></p><p>使用<strong>CDN</strong>已支持<strong>国内</strong>和<strong>国外</strong>加速服务器环境安装和预制文件下载，但国内受CDN连通性或国内机器带宽大小的限制加载可能会慢很多</p><p>融合怪测试说明以及部分测试结果的内容解释(初次使用推荐查看)：</p><p>除了已标注的原创内容，其余所有分区均为借鉴并进行优化修改后的版本，与原始对应的脚本不一样</p><p>所有检测都有考虑过使用并行测试，并在部分环节使用了该技术，比正常的顺序执行优化了2~3分钟，属于是独有的，暂无哪家的测试有同类技术</p><p>系统基础信息测试融合了多家还有我自己修补的部分检测(systl、NAT类型检测，并发ASN检测等)，应该是目前最全面最通用的了</p><p>CPU测试默认使用sysbench测试得分，不是yabs的gb4或gb5(虽然默认不是geekbench但可以通过指令指定geekbench常见版本进行测试)，前者只是简单的计算质数测试速度快，后者geekbench是综合测试系统算加权得分</p><p>使用sysbench测试得分是每秒处理的事件数目，这个指标无论在强还是弱性能的服务器上都能迅速测出来，而geekbench很多是测不动或者速度很慢起码2分半钟</p><p>CPU测试单核sysbench得分在5000以上的可以算第一梯队，4000到5000分算第二梯队，每1000分算一档，自己看看自己在哪个档位吧</p><p>AMD的7950x单核满血性能得分在6500左右，AMD的5950x单核满血性能得分5700左右，Intel普通的CPU(E5之类的)在1000~800左右，低于500的单核CPU可以说是性能比较烂的了</p><p>IO测试收录了两种，来源于lemonbench的dd磁盘测试和yabs的fio磁盘测试，综合来看会比较好，前者可能误差偏大但测试速度快无硬盘大小限制，后者真实一点但测试速度慢有硬盘以及内存大小限制</p><p>流媒体测试收录了两种，一个是go编译的二进制文件和一个shell脚本版本，二者各有优劣，互相对比看即可</p><p>tiktok测试有superbench和lmc999两种版本，哪个失效了随时可能更新为其中一种版本，以最新的脚本为准</p><p>回程路由测试选用的GO编译的二进制版本和朋友PR的版本，本人做了优化适配多个IP列表以及融合部分查询</p><p>IP质量检测纯原创，如有bug或者更多数据库来源可在issues中提出，日常看IP2Location数据库的IP类型即可，其中的25端口邮箱可达，则可搭建邮局</p><p>融合怪的IP质量检测是简化过的，没有查询Cloudflare的威胁得分，个人原创区的IP质量检测才是完整版(或者仓库说明中列出的那个IP质量检测的命令也是完整版)</p><p>三网测速使用自写的测速脚本，尽量使用最新节点最新组件进行测速，且有备用第三方go版本测速内核，做到自更新测速节点列表，自适应系统环境测速</p><p>其他第三方脚本归纳到了第三方脚本区，里面有同类型脚本不同作者的各种竞品脚本，如果融合怪不能使你满意或者有错误，可以看看那部分</p><p>原创脚本区是个人原创的部分，有事没事也可以看看，可能会更新某些偏门或者独到的脚本</p><p>VPS测试，VPS测速，VPS综合性能测试，VPS回程线路测试，VPS流媒体测试等所有测试融合的脚本，本脚本能融合的都融合了</p><h2 id="融合怪功能" tabindex="-1">融合怪功能 <a class="header-anchor" href="#融合怪功能" aria-label="Permalink to &quot;融合怪功能&quot;">​</a></h2><ul><li>[x] 自由组合测试方向和单项测试以及合集收录第三方脚本，融合怪各项测试均自优化修复过，与原始脚本均不同</li><li>[x] 基础信息查询--感谢<a href="https://github.com/teddysun/across/blob/master/bench.sh" target="_blank" rel="noreferrer">bench.sh</a>、<a href="https://www.oldking.net/350.html" target="_blank" rel="noreferrer">superbench.sh</a>、<a href="https://github.com/masonr/yet-another-bench-script" target="_blank" rel="noreferrer">yabs</a>、<a href="https://github.com/LemonBench/LemonBench" target="_blank" rel="noreferrer">lemonbench</a>开源，本人整理修改优化，同原版均不一致</li><li>[x] CPU测试--感谢<a href="https://github.com/LemonBench/LemonBench" target="_blank" rel="noreferrer">lemonbench</a>和<a href="https://github.com/masonr/yet-another-bench-script" target="_blank" rel="noreferrer">yabs</a>开源，本人整理修改优化</li><li>[x] 内存测试--感谢<a href="https://github.com/LemonBench/LemonBench" target="_blank" rel="noreferrer">lemonbench</a>开源，本人整理修改优化</li><li>[x] 磁盘dd读写测试--感谢<a href="https://github.com/LemonBench/LemonBench" target="_blank" rel="noreferrer">lemonbench</a>开源，本人整理修改优化</li><li>[x] 硬盘fio读写测试--感谢<a href="https://github.com/masonr/yet-another-bench-script" target="_blank" rel="noreferrer">yabs</a>开源，本人整理修改优化</li><li>[x] 御三家流媒体解锁测试--感谢<a href="https://github.com/sjlleo/netflix-verify" target="_blank" rel="noreferrer">netflix-verify</a>、<a href="https://github.com/sjlleo/VerifyDisneyPlus" target="_blank" rel="noreferrer">VerifyDisneyPlus</a>、<a href="https://github.com/sjlleo/TubeCheck" target="_blank" rel="noreferrer">TubeCheck</a>开源，本人整理修改维护<a href="https://github.com/oneclickvirt/CommonMediaTests" target="_blank" rel="noreferrer">CommonMediaTests</a>使用</li><li>[x] 常用流媒体解锁测试--感谢<a href="https://github.com/lmc999/RegionRestrictionCheck" target="_blank" rel="noreferrer">RegionRestrictionCheck</a>开源，本人整理修改优化</li><li>[x] Tiktok解锁--感谢<a href="https://github.com/lmc999/TikTokCheck" target="_blank" rel="noreferrer">TikTokCheck</a>开源，本人整理修改优化</li><li>[x] 三网回程以及路由延迟--感谢<a href="https://github.com/zhanghanyun/backtrace" target="_blank" rel="noreferrer">zhanghanyun/backtrace</a>开源，本人整理修改维护<a href="https://github.com/oneclickvirt/backtrace" target="_blank" rel="noreferrer">oneclickvirt/backtrace</a>使用</li><li>[x] 回程路由及带宽类型检测(商宽/家宽/数据中心)--由<a href="https://github.com/fscarmen" target="_blank" rel="noreferrer">fscarmen</a>的PR以及本人的技术思路提供，本人修改优化维护</li><li>[x] IP质量(含IPV4和IPV6)与邮件端口检测--使用<a href="https://github.com/oneclickvirt/securityCheck" target="_blank" rel="noreferrer">oneclickvirt/securityCheck</a>和<a href="https://github.com/oneclickvirt/portchecker" target="_blank" rel="noreferrer">oneclickvirt/portchecker</a>进行测试，感谢互联网提供的查询资源</li><li>[x] speedtest测速--使用自写<a href="https://github.com/spiritLHLS/ecsspeed" target="_blank" rel="noreferrer">ecsspeed</a>仓库，自动更新测速服务器ID，一劳永逸解决老是要手动更新测速ID的问题</li></ul><h1 id="友链" tabindex="-1">友链 <a class="header-anchor" href="#友链" aria-label="Permalink to &quot;友链&quot;">​</a></h1><h2 id="测评频道" tabindex="-1">测评频道 <a class="header-anchor" href="#测评频道" aria-label="Permalink to &quot;测评频道&quot;">​</a></h2><h3 id="https-t-me-vps-reviews" tabindex="-1"><a href="https://t.me/vps_reviews" target="_blank" rel="noreferrer">https://t.me/vps_reviews</a> <a class="header-anchor" href="#https-t-me-vps-reviews" aria-label="Permalink to &quot;https://t.me/vps_reviews&quot;">​</a></h3><h2 id="自动更新测速服务器节点列表的网络基准测试脚本" tabindex="-1">自动更新测速服务器节点列表的网络基准测试脚本 <a class="header-anchor" href="#自动更新测速服务器节点列表的网络基准测试脚本" aria-label="Permalink to &quot;自动更新测速服务器节点列表的网络基准测试脚本&quot;">​</a></h2><h3 id="https-github-com-spiritlhls-ecsspeed" tabindex="-1"><a href="https://github.com/spiritLHLS/ecsspeed" target="_blank" rel="noreferrer">https://github.com/spiritLHLS/ecsspeed</a> <a class="header-anchor" href="#https-github-com-spiritlhls-ecsspeed" aria-label="Permalink to &quot;https://github.com/spiritLHLS/ecsspeed&quot;">​</a></h3><h1 id="脚本概况" tabindex="-1">脚本概况 <a class="header-anchor" href="#脚本概况" aria-label="Permalink to &quot;脚本概况&quot;">​</a></h1><p>主界面：</p><p><img src="https://github.com/spiritLHLS/ecs/assets/103393591/051f1a83-ecd6-4713-af2f-c8b494e33c7f" alt="图片"></p><p>选项1融合怪完全体：</p><p><img src="https://github.com/spiritLHLS/ecs/assets/103393591/a769cb11-b416-4d40-a78c-265549bc4d49" alt="图片"><img src="https://github.com/spiritLHLS/ecs/assets/103393591/291854bf-4760-4a7f-8fad-33a114a2ba46" alt="图片"><img src="https://github.com/spiritLHLS/ecs/assets/103393591/6cad0c32-2409-4a92-b2c7-435f8eb66b3c" alt="图片"><img src="https://github.com/spiritLHLS/ecs/assets/103393591/e5e486e8-0791-43d6-919e-63b420cec022" alt="图片"><img src="https://github.com/spiritLHLS/ecs/assets/103393591/7296621e-76c0-41f1-bd9c-e3e696301dcc" alt="图片"><img src="https://github.com/spiritLHLS/ecs/assets/103393591/08289d71-9f91-4597-bcb1-0909622e16d4" alt="图片"><img src="https://github.com/spiritLHLS/ecs/assets/103393591/3a53758e-5fab-4fc5-a0b6-651c2f6b79a3" alt="图片"></p><p>选项6原创区：</p><p><img src="https://github.com/spiritLHLS/ecs/assets/103393591/393db695-5c94-41a9-9b02-812ad9d64967" alt="图片"></p>',87),l=[p];function n(c,r,i,h,d,b){return e(),a("div",null,l)}const m=s(o,[["render",n]]);export{y as __pageData,m as default};
