import{_ as e,v as n,b as s,R as o}from"./chunks/framework.70afa331.js";const u=JSON.parse('{"title":"Running Android Virtual Machine in Docker","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"en/guide/docker/docker_android.md","filePath":"en/guide/docker/docker_android.md","lastUpdated":1747704230000}'),a={name:"en/guide/docker/docker_android.md"},t=o(`<h1 id="running-android-virtual-machine-in-docker" tabindex="-1">Running Android Virtual Machine in Docker <a class="header-anchor" href="#running-android-virtual-machine-in-docker" aria-label="Permalink to &quot;Running Android Virtual Machine in Docker&quot;">​</a></h1><h2 id="one-click-deployment" tabindex="-1">One-Click Deployment <a class="header-anchor" href="#one-click-deployment" aria-label="Permalink to &quot;One-Click Deployment&quot;">​</a></h2><ul><li>Customizable Android version</li><li>Automatically creates a validated web website</li><li>Automatically installs Nginx and configures reverse proxy. Option to bind a domain is available. Default is port 80 if no domain binding is chosen.</li><li>No need to worry about host machine supporting nested virtualization</li><li>Supports x86_64 and ARM architectures</li></ul><p><strong>Host machine should have a minimum configuration of 1 core, 2GB RAM, and 15GB storage, else starting the setup might lead to host machine freezing.</strong></p><p>Ubuntu is recommended for hosting, Debian may cause white screen on Android.</p><p>Newer Android versions occupy more resources. The above configuration requirements are for the minimum version of Android. (Personally tested with the tag &quot;12.0.0-latest&quot;, higher versions resulted in a blank screen. Test which version works for you.)</p><p>If, after 5 minutes after opening, the login verification of the browser page still keeps failing, then please check the logs of the Android container, it is probable that the Android container has crashed, and it is recommended to replace the container with a lower version of the Android system for installation.</p><p><strong>Setup:</strong></p><p>Command:</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-L</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://raw.githubusercontent.com/oneclickvirt/docker/main/scripts/create_android.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">create_android.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">create_android.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">create_android.sh</span></span></code></pre></div><p>After executing the command, follow the prompts to input the required information. Make sure to choose the version by entering the corresponding number index. Once the installation is complete, open <code>YourMachineIPv4+80Port</code> to log in.</p><p>If you need to check the generated Android information and web login details, execute <code>cat /root/android_info</code> to retrieve the information.</p><p>Default username: <code>onea</code> Default password: <code>oneclick</code></p><p>For remote desktop, click on <code>H264 Converter</code> to initiate the redirection.</p><p><strong>Please note that currently only one Android container can be generated. Do not generate duplicates. If you need to replace the version, please execute the subsequent commands after deletion.</strong></p><p><strong>Temporarily only support the opening has been used, can not restart the server, reboot may not be able to restart after the mapping success, self-test</strong></p><p><strong>Deletion:</strong></p><ul><li>Delete the container</li><li>Delete the corresponding container image</li><li>Delete the configuration file</li></ul><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">docker</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">rm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-f</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">android</span></span>
<span class="line"><span style="color:#FFCB6B;">docker</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">rmi</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">$(</span><span style="color:#FFCB6B;">docker</span><span style="color:#C3E88D;"> images </span><span style="color:#89DDFF;">|</span><span style="color:#C3E88D;"> </span><span style="color:#FFCB6B;">grep</span><span style="color:#C3E88D;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">redroid</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;"> </span><span style="color:#89DDFF;">|</span><span style="color:#C3E88D;"> </span><span style="color:#FFCB6B;">awk</span><span style="color:#C3E88D;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">{print $3}</span><span style="color:#89DDFF;">&#39;)</span></span>
<span class="line"><span style="color:#FFCB6B;">rm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-rf</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/etc/nginx/sites-enabled/reverse-proxy</span></span>
<span class="line"><span style="color:#FFCB6B;">rm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-rf</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/etc/nginx/sites-available/reverse-proxy</span></span>
<span class="line"><span style="color:#FFCB6B;">rm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-rf</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/etc/nginx/passwd_scrcpy_web</span></span>
<span class="line"><span style="color:#FFCB6B;">rm</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-rf</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/root/android_info</span></span></code></pre></div>`,19),r=[t];function l(p,i,c,d,y,C){return n(),s("div",null,r)}const D=e(a,[["render",l]]);export{u as __pageData,D as default};
