import{_ as e,v as t,b as o,R as s}from"./chunks/framework.70afa331.js";const a="/assets/a1.662d9415.png",n="/assets/a2.1d7d86db.png",i="/assets/a3.178777a1.png",r="/assets/a4.65c15963.png",p="/assets/a5.01b8514f.png",c="/assets/a6.67efded0.png",d="/assets/a7.b8de9a6c.png",l="/assets/a8.69056d9a.png",h="/assets/a9.d4cc5fc0.png",m="/assets/a10.36860678.png",g="/assets/a11.bbc20080.png",u="/assets/a12.da46fe54.png",f="/assets/a13.b49276aa.png",b="/assets/a14.908480a6.png",_="/assets/a15.9533f30d.png",y="/assets/a16.2257f2d8.png",k="/assets/a17.b57c7755.png",w="/assets/a18.c3a22602.png",v="/assets/a19.1eefd81d.png",x="/assets/a20.24042237.png",I="/assets/a21.2bb37c9a.png",C="/assets/a22.44ab6d75.png",A="/assets/a23.191de0ca.png",S="/assets/a24.d2a9c729.png",q="/assets/a25.3e24d99b.png",P="/assets/a26.2fce6332.png",O="/assets/ar.0d8bb8f3.png",T="/assets/a27.dd61884c.png",V="/assets/a31.6d3a9d85.png",z="/assets/a32.d9973307.png",E="/assets/a33.8b54ce3f.png",M="/assets/a34.07f5cc75.png",U="/assets/a35.78be61d4.png",G="/assets/a36.a36765c2.png",D="/assets/a37.e6740eaa.png",F="/assets/a38.999b4ffb.png",B="/assets/a39.9dac95e1.png",K="/assets/a40.7f5ad4ea.png",R="/assets/a41.bbc5e317.png",N="/assets/a42.de7d1da7.png",Y="/assets/a43.e713599d.png",W="/assets/a44.5c4e5e0a.png",L="/assets/a45.2c32612e.png",H="/assets/b1.c1fdb506.png",j="/assets/b2.519a9bf0.png",Q="/assets/b3.42fd37a2.png",$="/assets/b4.b6d283a7.png",J="/assets/b5.4cd5361f.png",X="/assets/b6.36f0470d.png",Z="/assets/b7.9714bb16.png",ee="/assets/b8.2eb373bf.png",te="/assets/b9.3f01ffdc.png",oe="/assets/b10.52a93636.png",se="/assets/b11.2e525d8d.png",ae="/assets/b12.54c3189d.png",ne="/assets/b13.3b3ae784.png",ie="/assets/b14.662b1a1b.png",re="/assets/b15.0c5536d8.png",pe="/assets/b16.0fbcfcb1.png",ce="/assets/b17.4e7429fe.png",de="/assets/b18.8211f293.png",le="/assets/b19.2128d0fa.png",he="/assets/b20.8a62698b.png",me="/assets/b21.dfe1e8f0.png",ge="/assets/b22.13e3438c.png",ue="/assets/b23.e293a560.png",fe="/assets/b24.23cbc29a.png",be="/assets/b25.f2c2ebd7.png",_e="/assets/b26.b7136167.png",ye="/assets/b27.cc8f8644.png",ke="/assets/b28.2bf4d8b9.png",we="/assets/b29.8d301c10.png",ve="/assets/b30.a50bc8f3.png",xe="/assets/b31.4881ca1d.png",Ie="/assets/b32.b857654b.png",Ce="/assets/b33.1125a81c.png",Ae="/assets/b34.2101ce9a.png",Se="/assets/b35.83c5a766.png",qe="/assets/b36.ea1f048c.png",Pe="/assets/b37.b60405a9.png",Oe="/assets/br.bb3418b4.png",Te="/assets/b38.4d73fbe3.png",Ve="/assets/b39.c59ccabe.png",ze="/assets/b40.c1d0ee00.png",Ee="/assets/b41.0c6d7432.png",Me="/assets/b42.66cc353b.png",Ue="/assets/b43.a775b641.png",Ge="/assets/b44.2d540844.png",De="/assets/b45.fe0240c6.png",Fe="/assets/b46.f6b9da14.png",Je=JSON.parse('{"title":"Android Virtual Machine","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"en/guide/pve/pve_android.md","filePath":"en/guide/pve/pve_android.md","lastUpdated":1747748954000}'),Be={name:"en/guide/pve/pve_android.md"},Ke=s('<h1 id="android-virtual-machine" tabindex="-1">Android Virtual Machine <a class="header-anchor" href="#android-virtual-machine" aria-label="Permalink to &quot;Android Virtual Machine&quot;">​</a></h1><h2 id="android-system-image-download" tabindex="-1">Android System Image Download <a class="header-anchor" href="#android-system-image-download" aria-label="Permalink to &quot;Android System Image Download&quot;">​</a></h2><p>Download links for Android system version 9 and earlier:</p><p>International</p><p><a href="https://www.fosshub.com/Android-x86.html" target="_blank" rel="noreferrer">https://www.fosshub.com/Android-x86.html</a></p><p><a href="https://sourceforge.net/projects/android-x86/files/" target="_blank" rel="noreferrer">https://sourceforge.net/projects/android-x86/files/</a></p><p>Domestic (China)</p><p><a href="https://mirrors.tuna.tsinghua.edu.cn/osdn/android-x86/" target="_blank" rel="noreferrer">https://mirrors.tuna.tsinghua.edu.cn/osdn/android-x86/</a></p><p>Download links for Android system version 10 and later:</p><p><a href="https://blissos.org/index.html#download" target="_blank" rel="noreferrer">https://blissos.org/index.html#download</a></p><h2 id="virtual-machine-setup" tabindex="-1">Virtual Machine Setup <a class="header-anchor" href="#virtual-machine-setup" aria-label="Permalink to &quot;Virtual Machine Setup&quot;">​</a></h2><h3 id="setting-up-an-android-virtual-machine-with-version-9-and-earlier-images" tabindex="-1">Setting up an Android Virtual Machine with version 9 and earlier images <a class="header-anchor" href="#setting-up-an-android-virtual-machine-with-version-9-and-earlier-images" aria-label="Permalink to &quot;Setting up an Android Virtual Machine with version 9 and earlier images&quot;">​</a></h3><p>This guide demonstrates using:</p><p><a href="https://mirrors.tuna.tsinghua.edu.cn/osdn/android-x86/71931/android-x86_64-9.0-rc2.iso" target="_blank" rel="noreferrer">https://mirrors.tuna.tsinghua.edu.cn/osdn/android-x86/71931/android-x86_64-9.0-rc2.iso</a></p><p>as an example</p><p><img src="'+a+'" alt="1"></p><h4 id="template-configuration" tabindex="-1">Template Configuration <a class="header-anchor" href="#template-configuration" aria-label="Permalink to &quot;Template Configuration&quot;">​</a></h4><p>Create a virtual machine with VMID <code>100</code> and select the corresponding storage disk</p><p><img src="'+n+'" alt="2"></p><p>Select the Android image, then choose <code>Linux</code> for <code>Type</code> and choose an option with version 2.6 for <code>Version</code></p><p><img src="'+i+'" alt="3"></p><p>Select <code>Vmware compatible</code> for <code>Graphic card</code>, other options are default as shown</p><p><img src="'+r+'" alt="4"></p><p>Choose <code>SATA</code> for disk format, allocate at least 30GB of disk space (size can be chosen based on your needs)</p><p><img src="'+p+'" alt="5"></p><p>CPU count should be at least 2 cores</p><p>If your host machine supports nested virtualization, choose <code>host</code> for CPU type (verified to work)</p><p>If your host machine doesn&#39;t support nested virtualization, choose <code>qemu64</code> for CPU type, and after creating the template, go to <code>Options</code> and uncheck <code>KVM hardware virtualization</code> before starting the virtual machine (no guarantee that system initialization will work properly)</p><p><img src="'+c+'" alt="6"></p><p>Memory should be at least 4GB, enter at least <code>4096</code></p><p><img src="'+d+'" alt="7"></p><p>Select <code>vmbr1</code> for the network bridge, choose <code>VirtIO (paravirtualized)</code> for <code>Model</code>, and uncheck <code>Firewall</code></p><p><img src="'+l+'" alt="8"></p><p>After clicking the continue button, the template is successfully created. You need to manually start it and enter the VNC interface</p><p><img src="'+h+'" alt="9"></p><h4 id="system-installation" tabindex="-1">System Installation <a class="header-anchor" href="#system-installation" aria-label="Permalink to &quot;System Installation&quot;">​</a></h4><p>After entering the boot menu, select the option starting with <code>Installation</code></p><p><img src="'+m+'" alt="10"></p><p>Then you need to create disk partitions. If you can&#39;t select it, press the keyboard button &#39;c&#39; to select it</p><p><img src="'+g+'" alt="11"></p><p>Choose not to use GPT format</p><p><img src="'+u+'" alt="12"></p><p>When the empty disk is displayed, select <code>New</code></p><p><img src="'+f+'" alt="13"></p><p>Select partition type <code>Primary</code></p><p><img src="'+b+'" alt="14"></p><p>Then you&#39;ll be prompted to specify the size of the space. Just press Enter to use all available space</p><p><img src="'+_+'" alt="15"></p><p>Then select <code>Flags</code>, move to <code>Bootable</code>, press Enter until <code>Boot</code> appears under <code>Flags</code></p><p><img src="'+y+'" alt="16"></p><p>Then move to <code>Write</code> and press Enter to write changes</p><p><img src="'+k+'" alt="17"></p><p>You&#39;ll be asked to confirm whether to continue. Type <code>yes</code> and press Enter to continue</p><p><img src="'+w+'" alt="18"></p><p>After a progress bar completes, you&#39;ll return to the initial menu. Select <code>Quit</code> to exit the menu, everything is now ready</p><p><img src="'+v+'" alt="19"></p><p>Then you&#39;ll return to the UI interface. Use the previously initialized disk and select the <code>OK</code> button</p><p><img src="'+x+'" alt="20"></p><p>Use the arrow keys to select <code>ext4</code> file system type, then select the <code>OK</code> button</p><p><img src="'+I+'" alt="21"></p><p>Confirm formatting by selecting the <code>Yes</code> button</p><p><img src="'+C+'" alt="22"></p><p>Confirm GRUB boot installation by selecting the <code>OK</code> button</p><p><img src="'+A+'" alt="23"></p><p>Confirm the file system is readable and writable by selecting the <code>OK</code> button</p><p><img src="'+S+'" alt="24"></p><p>After a progress bar runs for some time, it will show that installation was successful. Select <code>Reboot</code> and the <code>OK</code> button</p><p><img src="'+q+'" alt="25"></p><p>After a while, you&#39;ll enter the Android logo screen</p><p><img src="'+P+'" alt="26"></p><h4 id="removing-the-image" tabindex="-1">Removing the Image <a class="header-anchor" href="#removing-the-image" aria-label="Permalink to &quot;Removing the Image&quot;">​</a></h4><p>Then there will be a black screen. If it&#39;s still black after 3-5 minutes, execute <code>qm stop 100</code> on the host machine, then start removing the image file.</p><p>Remove the installed ISO file by manually clicking on the corresponding <code>CD</code> in the <code>Hardware</code> section of the ProxmoxVE web interface and selecting <code>Remove</code></p><p><img src="'+O+'" alt="ar"></p><p>Then execute <code>qm start 100</code> on the host machine again, and you should enter the Android system initialization interface</p><h4 id="system-initialization" tabindex="-1">System Initialization <a class="header-anchor" href="#system-initialization" aria-label="Permalink to &quot;System Initialization&quot;">​</a></h4><p>In the initialization interface, select the language and click the confirm button</p><p><img src="'+T+'" alt="27"></p><p>It will try to connect to WiFi. Since our PVE installation uses a completely static network configuration, just skip this and set it up later</p><p><img src="'+V+'" alt="31"></p><p>Click the next button to confirm the time settings</p><p><img src="'+z+'" alt="32"></p><p>Click to cancel screen protection, confirm to skip again</p><p><img src="'+E+'" alt="33"></p><p>After a period of black screen, the main screen application selection will appear. If this interface doesn&#39;t appear after waiting 3-5 minutes, restart the virtual machine as before, and it should display after entering VNC</p><p>Select <code>Quickstep</code></p><p><img src="'+M+'" alt="34"></p><h4 id="network-settings" tabindex="-1">Network Settings <a class="header-anchor" href="#network-settings" aria-label="Permalink to &quot;Network Settings&quot;">​</a></h4><p>After entering the desktop, you need to set up the network. Click the settings icon in the upper left corner</p><p><img src="'+U+'" alt="35"></p><p>A dropdown appears, click and hold to pull down</p><p><img src="'+G+'" alt="36"></p><p>Click the gear button</p><p><img src="'+D+'" alt="37"></p><p>Enter <code>Network &amp; Internet</code> settings</p><p><img src="'+F+'" alt="38"></p><p>Double-click <code>WIFI</code> with the left mouse button</p><p><img src="'+B+'" alt="39"></p><p>Modify the detected <code>VirtWIFI</code>. Right-click to bring up the selection box, then click <code>Modify network</code></p><p><img src="'+K+'" alt="40"></p><p>Advanced options appear, click to open hidden options</p><p><img src="'+R+'" alt="41"></p><p>Configure the network settings as shown</p><p>IP address <code>172.16.1.xxx</code> (replace xxx with your desired IP, I used 100 because my vmid is 100 for convenience)</p><p>Subnet mask <code>24</code></p><p>Default gateway <code>172.16.1.1</code></p><p>DNS <code>8.8.8.8</code> or <code>144.144.144.144</code></p><p>Then click the save button</p><p><img src="'+N+'" alt="42"></p><p>At this point, the settings may still not be applied. Turn off WiFi and then turn it back on, it should then show as connected</p><p><img src="'+Y+'" alt="43"></p><p>Then exit to the main interface, click on Google Chrome, and try to open a webpage</p><p><img src="'+W+'" alt="44"></p><p>You can see that opening this guide&#39;s URL works without problems, proving that the network is connected</p><p><img src="'+L+'" alt="45"></p><h3 id="setting-up-an-android-virtual-machine-with-version-10-and-later-images" tabindex="-1">Setting up an Android Virtual Machine with version 10 and later images <a class="header-anchor" href="#setting-up-an-android-virtual-machine-with-version-10-and-later-images" aria-label="Permalink to &quot;Setting up an Android Virtual Machine with version 10 and later images&quot;">​</a></h3><p>This guide demonstrates using:</p><p><a href="https://psychz.dl.sourceforge.net/project/blissos-x86/Official/BlissOS15/Gapps/Generic/Bliss-v15.9.2-x86_64-OFFICIAL-gapps-20241012.iso?viasf=1" target="_blank" rel="noreferrer">https://psychz.dl.sourceforge.net/project/blissos-x86/Official/BlissOS15/Gapps/Generic/Bliss-v15.9.2-x86_64-OFFICIAL-gapps-20241012.iso?viasf=1</a></p><p>as an example</p><p><img src="'+H+'" alt="1"></p><h4 id="template-configuration-1" tabindex="-1">Template Configuration <a class="header-anchor" href="#template-configuration-1" aria-label="Permalink to &quot;Template Configuration&quot;">​</a></h4><p>The host machine needs to execute the following command to install graphics environment dependencies</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">apt</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">libgl1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">libegl1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-y</span></span></code></pre></div><p>Then start creating the virtual machine by clicking the <code>Create VM</code> button in the upper right corner. Fill in <code>VMID</code>, <code>Name</code>, <code>Resource Pool</code></p><p><img src="'+j+'" alt="2"></p><p>Select the Android image, then choose <code>Linux</code> for <code>Type</code> and an option with version 2.6 for <code>Version</code></p><p><img src="'+Q+'" alt="3"></p><p>If the host machine has a GPU, select <code>VirGL GPU</code> for <code>Graphic card</code>.</p><p>If the host machine doesn&#39;t have a GPU, select <code>VirtIO</code> or <code>Vmware compatible</code> for <code>Graphic card</code>.</p><p>Select <code>q35</code> for <code>Machine</code>, select <code>OVMF (UEFI)</code> for <code>BIOS</code>, and select <code>local</code> for <code>EFI Storage</code>.</p><p><img src="'+$+'" alt="4"></p><p>Choose <code>SATA</code> for disk format. How much disk space to allocate is up to you, but at least 30GB is recommended</p><p><img src="'+J+'" alt="5"></p><p>CPU count should be at least 2 cores</p><p>If your host machine supports nested virtualization, choose <code>host</code> for CPU type (verified to work)</p><p>If your host machine doesn&#39;t support nested virtualization, choose <code>qemu64</code> for CPU type, and after creating the template, go to <code>Options</code> and uncheck <code>KVM hardware virtualization</code> before starting the virtual machine (no guarantee that system initialization will work properly)</p><p><img src="'+X+'" alt="6"></p><p>Memory should be at least 4GB, enter at least <code>4096</code></p><p><img src="'+Z+'" alt="7"></p><p>Select <code>vmbr1</code> for the network bridge, choose <code>VirtIO (paravirtualized)</code> for <code>Model</code>, and uncheck <code>Firewall</code></p><p><img src="'+ee+'" alt="8"></p><p>After clicking the continue button, the template is successfully created. You need to manually start it and enter the VNC interface.</p><h4 id="system-installation-1" tabindex="-1">System Installation <a class="header-anchor" href="#system-installation-1" aria-label="Permalink to &quot;System Installation&quot;">​</a></h4><p>After entering the boot menu, select the option ending with <code>Installation</code></p><p><img src="'+te+'" alt="9"></p><p>Then you need to create disk partitions. If you can&#39;t select it, press the keyboard button &#39;c&#39; to select it.</p><p><img src="'+oe+'" alt="10"></p><p>Choose not to use GPT format, continue using cfdisk format.</p><p><img src="'+se+'" alt="11"></p><p>When the format selection box appears, select <code>gpt</code>.</p><p><img src="'+ae+'" alt="12"></p><p>When the empty disk is displayed, select <code>New</code>.</p><p><img src="'+ne+'" alt="13"></p><p>Because you need to create an EFI partition, you need to change the size rather than using the default. Change the number to 1 to allocate 1GB of disk space, then press Enter.</p><p><img src="'+ie+'" alt="14"></p><p>Then you&#39;ll return to the menu bar. Use the arrow keys to select <code>Type</code>, then press Enter to choose the format type for the partition</p><p><img src="'+re+'" alt="15"></p><p>Use the arrow keys to select the first option <code>EFI System</code>, then press Enter</p><p><img src="'+pe+'" alt="16"></p><p>Now you&#39;re back at the menu bar. In the upper half of the page, you can see the specific partition size and format</p><p><img src="'+ce+'" alt="17"></p><p>Then press the down arrow key to select the next empty partition line</p><p><img src="'+de+'" alt="18"></p><p>Select <code>New</code> from the menu bar, then press Enter</p><p><img src="'+le+'" alt="19"></p><p>Here you need to fill in the partition size. Match it with the empty partition size shown in the upper right corner, then press Enter.</p><p><img src="'+he+'" alt="20"></p><p>Return to the menu bar, select <code>Write</code>, press Enter to write the partition.</p><p><img src="'+me+'" alt="21"></p><p>You&#39;ll be prompted to enter <code>yes</code> to confirm. Enter it and press Enter.</p><p><img src="'+ge+'" alt="22"></p><p>After a progress bar runs, you should return to the menu bar. Select <code>Quit</code> to exit the menu, everything is now ready</p><p><img src="'+ue+'" alt="23"></p><p>Then you&#39;ll return to the UI interface. Use the previously initialized 1GB disk and select the <code>OK</code> button</p><p><img src="'+fe+'" alt="24"></p><p>Use the arrow keys to select <code>fat32</code> file system type, then select the <code>OK</code> button</p><p><img src="'+be+'" alt="25"></p><p>Confirm not to modify the name by selecting the <code>OK</code> button</p><p><img src="'+_e+'" alt="26"></p><p>Confirm formatting by selecting the <code>Yes</code> button</p><p><img src="'+ye+'" alt="27"></p><p>Then you&#39;ll return to the initial UI bar. Select the second partition that is not 1GB in size and select the <code>OK</code> button</p><p><img src="'+ke+'" alt="28"></p><p>Use the arrow keys to select <code>ext4</code> file system type, then select the <code>OK</code> button</p><p><img src="'+we+'" alt="29"></p><p>Confirm not to modify the name by selecting the <code>OK</code> button</p><p><img src="'+ve+'" alt="30"></p><p>Confirm formatting by selecting the <code>Yes</code> button</p><p><img src="'+xe+'" alt="31"></p><p>Choose not to use extra space for updates by selecting the <code>No</code> button</p><p><img src="'+Ie+'" alt="32"></p><p>Confirm <code>GRUB2 EFI BootLoader</code> by selecting the <code>OK</code> button</p><p><img src="'+Ce+'" alt="33"></p><p>After a progress bar runs for some time, it will show that installation was successful. Select <code>Reboot</code> and the <code>OK</code> button.</p><p><img src="'+Ae+'" alt="34"></p><h4 id="system-initialization-1" tabindex="-1">System Initialization <a class="header-anchor" href="#system-initialization-1" aria-label="Permalink to &quot;System Initialization&quot;">​</a></h4><p>After a while, you&#39;ll enter the logo screen.</p><p><img src="'+Se+'" alt="35"></p><p>After some more time, you should enter the initial Android interface. You&#39;ll see a popup and the time. Hold the left mouse button and swipe up</p><p><img src="'+qe+'" alt="36"></p><p>The main screen application selection will appear. Select <code>Quickstep</code></p><p>If this interface doesn&#39;t appear after waiting 3-5 minutes, restart the virtual machine, and it should display after entering VNC</p><p><img src="'+Pe+'" alt="37"></p><h4 id="removing-the-image-1" tabindex="-1">Removing the Image <a class="header-anchor" href="#removing-the-image-1" aria-label="Permalink to &quot;Removing the Image&quot;">​</a></h4><p>After the Android interface appears, execute <code>qm stop 100</code> on the host machine, then start removing the image file.</p><p>Remove the installed ISO file by manually clicking on the corresponding <code>CD</code> in the <code>Hardware</code> section of the ProxmoxVE web interface and selecting <code>Remove</code></p><p><img src="'+Oe+'" alt="br"></p><p>Then execute <code>qm start 100</code> on the host machine again, and you should enter the system&#39;s initial interface.</p><h4 id="network-settings-1" tabindex="-1">Network Settings <a class="header-anchor" href="#network-settings-1" aria-label="Permalink to &quot;Network Settings&quot;">​</a></h4><p>After entering the desktop, you need to set up the network. Hold the left mouse button in the indicated position and swipe up</p><p><img src="'+Te+'" alt="38"></p><p>A bunch of applications will pop up. Find the one called <code>Bliss Ethernet Manager</code> and click to open it</p><p><img src="'+Ve+'" alt="39"></p><p>Click to open <code>IP Assignment</code>, select <code>Static</code> type, click the OK button to confirm</p><p><img src="'+ze+'" alt="40"></p><p>Click to open <code>IP Address</code>, enter <code>172.16.1.xxx/24</code> (I used 100 for xxx because my virtual machine VMID is 100 for easy identification), click the OK button to confirm</p><p><img src="'+Ee+'" alt="41"></p><p>Click to open <code>Gateway Address</code>, enter <code>172.16.1.1</code>, click the OK button to confirm</p><p><img src="'+Me+'" alt="42"></p><p>Click to open <code>DNS Address</code>, enter <code>8.8.8.8</code>, click the OK button to confirm</p><p><img src="'+Ue+'" alt="43"></p><p>Then in the application interface, click <code>Interface Up</code> and <code>Refresh</code>, then press the <code>exit</code> key on the keyboard to exit the program</p><p><img src="'+Ge+'" alt="44"></p><p>Open the browser in the main interface to verify that the network is working properly</p><p><img src="'+De+'" alt="45"></p><p>You can see that opening this guide works without problems, the network is functioning</p><p><img src="'+Fe+'" alt="46"></p>',227),Re=[Ke];function Ne(Ye,We,Le,He,je,Qe){return t(),o("div",null,Re)}const Xe=e(Be,[["render",Ne]]);export{Je as __pageData,Xe as default};
