import{_ as e,v as t,b as a,R as s}from"./chunks/framework.70afa331.js";const o="/assets/a1.662d9415.png",n="/assets/a2.1d7d86db.png",i="/assets/a3.178777a1.png",r="/assets/a4.65c15963.png",p="/assets/a5.01b8514f.png",c="/assets/a6.67efded0.png",d="/assets/a7.b8de9a6c.png",l="/assets/a8.69056d9a.png",h="/assets/a9.d4cc5fc0.png",m="/assets/a10.36860678.png",g="/assets/a11.bbc20080.png",u="/assets/a12.da46fe54.png",_="/assets/a13.b49276aa.png",f="/assets/a14.908480a6.png",b="/assets/a15.9533f30d.png",w="/assets/a16.2257f2d8.png",k="/assets/a17.b57c7755.png",y="/assets/a18.c3a22602.png",v="/assets/a19.1eefd81d.png",x="/assets/a20.24042237.png",A="/assets/a21.2bb37c9a.png",I="/assets/a22.44ab6d75.png",S="/assets/a23.191de0ca.png",V="/assets/a24.d2a9c729.png",q="/assets/a25.3e24d99b.png",C="/assets/a26.2fce6332.png",P="/assets/ar.0d8bb8f3.png",T="/assets/a27.dd61884c.png",E="/assets/a31.6d3a9d85.png",M="/assets/a32.d9973307.png",N="/assets/a33.8b54ce3f.png",O="/assets/a34.07f5cc75.png",D="/assets/a35.78be61d4.png",F="/assets/a36.a36765c2.png",R="/assets/a37.e6740eaa.png",U="/assets/a38.999b4ffb.png",z="/assets/a39.9dac95e1.png",G="/assets/a40.7f5ad4ea.png",K="/assets/a41.bbc5e317.png",W="/assets/a42.de7d1da7.png",L="/assets/a43.e713599d.png",Y="/assets/a44.5c4e5e0a.png",$="/assets/a45.2c32612e.png",oe=JSON.parse('{"title":"Android Virtual Machine","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"en/guide/pve/pve_android.md","filePath":"en/guide/pve/pve_android.md","lastUpdated":1746843077000}'),j={name:"en/guide/pve/pve_android.md"},B=s('<h1 id="android-virtual-machine" tabindex="-1">Android Virtual Machine <a class="header-anchor" href="#android-virtual-machine" aria-label="Permalink to &quot;Android Virtual Machine&quot;">​</a></h1><h2 id="android-system-image-download" tabindex="-1">Android System Image Download <a class="header-anchor" href="#android-system-image-download" aria-label="Permalink to &quot;Android System Image Download&quot;">​</a></h2><p>Android system version 9 and earlier image download links:</p><p>International</p><p><a href="https://www.fosshub.com/Android-x86.html" target="_blank" rel="noreferrer">https://www.fosshub.com/Android-x86.html</a></p><p><a href="https://sourceforge.net/projects/android-x86/files/" target="_blank" rel="noreferrer">https://sourceforge.net/projects/android-x86/files/</a></p><p>China</p><p><a href="https://mirrors.tuna.tsinghua.edu.cn/osdn/android-x86/" target="_blank" rel="noreferrer">https://mirrors.tuna.tsinghua.edu.cn/osdn/android-x86/</a></p><p>Android system version 10 and later new image download links:</p><p><a href="https://blissos.org/index.html#download" target="_blank" rel="noreferrer">https://blissos.org/index.html#download</a></p><h2 id="visual-machine-setup" tabindex="-1">Visual Machine Setup <a class="header-anchor" href="#visual-machine-setup" aria-label="Permalink to &quot;Visual Machine Setup&quot;">​</a></h2><h3 id="setting-up-android-virtual-machine-with-android-version-9-and-earlier-images" tabindex="-1">Setting up Android Virtual Machine with Android Version 9 and Earlier Images <a class="header-anchor" href="#setting-up-android-virtual-machine-with-android-version-9-and-earlier-images" aria-label="Permalink to &quot;Setting up Android Virtual Machine with Android Version 9 and Earlier Images&quot;">​</a></h3><p>This guide demonstrates using</p><p><a href="https://mirrors.tuna.tsinghua.edu.cn/osdn/android-x86/71931/android-x86_64-9.0-rc2.iso" target="_blank" rel="noreferrer">https://mirrors.tuna.tsinghua.edu.cn/osdn/android-x86/71931/android-x86_64-9.0-rc2.iso</a></p><p>as an example</p><p><img src="'+o+'" alt="1"></p><h4 id="template-setup" tabindex="-1">Template Setup <a class="header-anchor" href="#template-setup" aria-label="Permalink to &quot;Template Setup&quot;">​</a></h4><p>Create a virtual machine with VMID <code>100</code> and select the corresponding storage disk</p><p><img src="'+n+'" alt="2"></p><p>Select the Android image, then choose <code>Linux</code> for <code>Type</code> and select the option containing version 2.6 for <code>Version</code></p><p><img src="'+i+'" alt="3"></p><p>Select <code>Vmware compatible</code> for <code>Graphic card</code>, other options as shown in the default image</p><p><img src="'+r+'" alt="4"></p><p>Select <code>SATA</code> for disk format, allocate disk size as needed (recommended at least 30G)</p><p><img src="'+p+'" alt="5"></p><p>Select <code>host</code> for CPU type. If your host machine doesn&#39;t support nested virtualization, please select <code>qemu64</code>, although success is not guaranteed in this case</p><p><img src="'+c+'" alt="6"></p><p>Allocate at least 4G of memory, enter at least <code>4096</code></p><p><img src="'+d+'" alt="7"></p><p>Select <code>vmbr1</code> for bridge binding, choose <code>VirtIO (paravirtualized)</code> for <code>Model</code>, and uncheck the <code>Firewall</code> option</p><p><img src="'+l+'" alt="8"></p><p>After clicking the continue button, the template is successfully created. You need to manually click start and enter the VNC interface</p><p><img src="'+h+'" alt="9"></p><h4 id="system-init" tabindex="-1">System Init <a class="header-anchor" href="#system-init" aria-label="Permalink to &quot;System Init&quot;">​</a></h4><p>After entering the boot screen, select the option starting with <code>Installation</code></p><p><img src="'+m+'" alt="10"></p><p>Next, you need to create and write to the disk. If you can&#39;t select it, press the &#39;c&#39; key on the keyboard to select it</p><p><img src="'+g+'" alt="11"></p><p>Choose not to use GPT format</p><p><img src="'+u+'" alt="12"></p><p>When the blank disk is displayed, select <code>New</code></p><p><img src="'+_+'" alt="13"></p><p>Select partition type <code>Primary</code></p><p><img src="'+f+'" alt="14"></p><p>You&#39;ll be prompted to allocate space, just press Enter to use all available space</p><p><img src="'+b+'" alt="15"></p><p>Then select <code>Flags</code>, move to <code>Bootable</code>, press Enter until <code>Boot</code> is displayed under <code>Flags</code></p><p><img src="'+w+'" alt="16"></p><p>Then move to <code>Write</code> and press Enter to write</p><p><img src="'+k+'" alt="17"></p><p>You&#39;ll be asked to confirm, type <code>yes</code> and press Enter to continue</p><p><img src="'+y+'" alt="18"></p><p>After a progress bar completes, you&#39;ll return to the initial menu. Select <code>Quit</code> to exit the menu, and everything is now ready</p><p><img src="'+v+'" alt="19"></p><p>Then you&#39;ll return to the UI interface. Use the previously initialized disk, select the <code>OK</code> button and press Enter</p><p><img src="'+x+'" alt="20"></p><p>Use the arrow keys to select <code>ext4</code> for the file system, then select the <code>OK</code> button and press Enter</p><p><img src="'+A+'" alt="21"></p><p>Confirm formatting, select the <code>OK</code> button and press Enter</p><p><img src="'+I+'" alt="22"></p><p>Confirm GRUB boot installation, select the <code>OK</code> button and press Enter</p><p><img src="'+S+'" alt="23"></p><p>Confirm the file system is readable and writable, select the <code>OK</code> button and press Enter</p><p><img src="'+V+'" alt="24"></p><p>After a progress bar runs for a while, it will show installation succeeded. Select <code>Reboot</code>, then the <code>OK</code> button and press Enter</p><p><img src="'+q+'" alt="25"></p><p>After a while, you&#39;ll enter the Android logo screen</p><p><img src="'+C+'" alt="26"></p><p>Then it will go into a black screen. If it remains black after 3-5 minutes, execute <code>qm stop 100</code> on the host machine, then begin removing the image file</p><p>Remove the ISO file that has been installed by manually clicking on the corresponding <code>CD</code> in the <code>Hardware</code> section of the ProxmoxVE web interface and selecting <code>Remove</code></p><p><img src="'+P+'" alt="ar"></p><p>Then execute <code>qm start 100</code> on the host machine, and you should enter the Android system initialization interface</p><p>Select your language in the initialization interface, then click the confirm button</p><p><img src="'+T+'" alt="27"></p><p>At this point it will try to connect to WIFI. Since this PVE installation uses completely static network configuration, skip this step and configure it later</p><p><img src="'+E+'" alt="31"></p><p>Click the next button to confirm time settings</p><p><img src="'+M+'" alt="32"></p><p>Click to cancel screen protection, confirm to skip again</p><p><img src="'+N+'" alt="33"></p><p>After a period of black screen, the main screen application selection will appear. If this interface still doesn&#39;t appear after waiting 3-5 minutes, restart the virtual machine as before, and it should display when you enter VNC again</p><p>Select <code>Quickstep</code></p><p><img src="'+O+'" alt="34"></p><h4 id="network-configuration" tabindex="-1">Network Configuration <a class="header-anchor" href="#network-configuration" aria-label="Permalink to &quot;Network Configuration&quot;">​</a></h4><p>Then you&#39;ll enter the desktop. Now you need to set up the network. Click the settings icon in the upper left corner</p><p><img src="'+D+'" alt="35"></p><p>A dropdown appears, click and hold to pull down</p><p><img src="'+F+'" alt="36"></p><p>Click the gear button</p><p><img src="'+R+'" alt="37"></p><p>Enter <code>Network &amp; Internet</code> settings</p><p><img src="'+U+'" alt="38"></p><p>Double-click <code>WIFI</code> with the left mouse button</p><p><img src="'+z+'" alt="39"></p><p>Modify the detected <code>VirtWIFI</code>, right-click to bring up the selection box, then click <code>Modify network</code></p><p><img src="'+G+'" alt="40"></p><p>Advanced options appear, click to open hidden options</p><p><img src="'+K+'" alt="41"></p><p>Configure the network settings as shown</p><p>IP address <code>172.16.1.xxx</code> (replace xxx with your desired IP; my vmid is 100, so I used 100 for convenience)</p><p>Subnet mask <code>24</code></p><p>Default gateway <code>172.16.1.1</code></p><p>DNS <code>8.8.8.8</code> or <code>144.144.144.144</code></p><p>Then click the save button</p><p><img src="'+W+'" alt="42"></p><p>At this point, the settings may still not be applied. Turn off WIFI and then enable it again, it should show connected</p><p><img src="'+L+'" alt="43"></p><p>Then exit to the main interface, click on Google Chrome, and try to open a webpage</p><p><img src="'+Y+'" alt="44"></p><p>You can see that opening this guide&#39;s URL works fine, proving the network is connected</p><p><img src="'+$+'" alt="45"></p><h3 id="setting-up-android-virtual-machine-with-android-version-10-and-later-images" tabindex="-1">Setting up Android Virtual Machine with Android Version 10 and Later Images <a class="header-anchor" href="#setting-up-android-virtual-machine-with-android-version-10-and-later-images" aria-label="Permalink to &quot;Setting up Android Virtual Machine with Android Version 10 and Later Images&quot;">​</a></h3><p>This guide demonstrates using</p><p>as an example</p>',114),Q=[B];function H(J,X,Z,ee,te,ae){return t(),a("div",null,Q)}const ne=e(j,[["render",H]]);export{oe as __pageData,ne as default};
