import{_ as s,v as e,b as o,R as a}from"./chunks/framework.70afa331.js";const m=JSON.parse('{"title":"","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"incomplete/webvirtcloud.md","filePath":"incomplete/webvirtcloud.md","lastUpdated":1745148121000}'),n={name:"incomplete/webvirtcloud.md"},t=a(`<h2 id="webvirtcloud-installation-guide" tabindex="-1">WebVirtCloud Installation Guide <a class="header-anchor" href="#webvirtcloud-installation-guide" aria-label="Permalink to &quot;WebVirtCloud Installation Guide&quot;">​</a></h2><p><a href="https://hits.spiritlhl.net" target="_blank" rel="noreferrer"><img src="https://hits.spiritlhl.net/webvirtcloud.svg?action=hit&amp;title=hits&amp;title_bg=%23555555&amp;count_bg=%233aebee&amp;edge_flat=false" alt="hits"></a></p><p>Repository: <a href="https://github.com/oneclickvirt/webvirtcloud" target="_blank" rel="noreferrer">https://github.com/oneclickvirt/webvirtcloud</a></p><h2 id="controller-installation-控制端安装" tabindex="-1">Controller Installation 控制端安装 <a class="header-anchor" href="#controller-installation-控制端安装" aria-label="Permalink to &quot;Controller Installation 控制端安装&quot;">​</a></h2><h3 id="system-requirements-系统要求" tabindex="-1">System Requirements 系统要求 <a class="header-anchor" href="#system-requirements-系统要求" aria-label="Permalink to &quot;System Requirements 系统要求&quot;">​</a></h3><p><strong>Minimum Configuration 最低配置要求:</strong></p><ul><li>CPU: 1 core / 1 核</li><li>Memory: 1 GB RAM / 内存: 1 GB</li><li>Disk: 10 GB free space / 硬盘: 空余 10 GB</li></ul><h3 id="default-login-information-默认登录信息" tabindex="-1">Default Login Information 默认登录信息 <a class="header-anchor" href="#default-login-information-默认登录信息" aria-label="Permalink to &quot;Default Login Information 默认登录信息&quot;">​</a></h3><ul><li>Username 用户名: <code>admin@webvirt.cloud</code></li><li>Password 密码: <code>admin</code></li></ul><h3 id="access-urls-面板地址" tabindex="-1">Access URLs 面板地址 <a class="header-anchor" href="#access-urls-面板地址" aria-label="Permalink to &quot;Access URLs 面板地址&quot;">​</a></h3><ul><li>Client Panel 用户面板: <code>https://192-168-0-114.nip.io</code></li><li>Admin Panel 管理后台: <code>https://192-168-0-114.nip.io/admin</code></li></ul><blockquote><p><strong>Note 注意:</strong> Replace <code>192.168.0.114</code> with your actual public IP address to get the correct access URL.<br> 请将 <code>192.168.0.114</code> 替换为你的公网 IP，以获取真实可访问地址。</p></blockquote><h3 id="installation-command-安装命令" tabindex="-1">Installation Command 安装命令 <a class="header-anchor" href="#installation-command-安装命令" aria-label="Permalink to &quot;Installation Command 安装命令&quot;">​</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-slk</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://raw.githubusercontent.com/oneclickvirt/webvirtcloud/main/scripts/install_webvirt_cloud.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install_webvirt_cloud.sh</span><span style="color:#A6ACCD;"> \\</span></span>
<span class="line"><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install_webvirt_cloud.sh</span><span style="color:#A6ACCD;"> \\</span></span>
<span class="line"><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install_webvirt_cloud.sh</span></span></code></pre></div><h2 id="hypervisor-installation-计算节点安装" tabindex="-1">Hypervisor Installation 计算节点安装 <a class="header-anchor" href="#hypervisor-installation-计算节点安装" aria-label="Permalink to &quot;Hypervisor Installation 计算节点安装&quot;">​</a></h2><blockquote><p><strong>Important 重要提示:</strong> The Hypervisor and Controller cannot be installed on the same virtual machine; network conflicts will occur.<br> Hypervisor 和 Controller 不能在同一个虚拟机上进行安装，否则网络会出现冲突问题。</p></blockquote><h3 id="system-requirements-系统要求-1" tabindex="-1">System Requirements 系统要求 <a class="header-anchor" href="#system-requirements-系统要求-1" aria-label="Permalink to &quot;System Requirements 系统要求&quot;">​</a></h3><p><strong>Recommended Configuration 建议配置:</strong></p><ul><li>CPU: 2 cores / 2 核</li><li>Memory: 4 GB RAM / 内存: 4 GB</li><li>Disk: 40 GB free space / 硬盘: 空余 40 GB</li></ul><blockquote><p><strong>Additional Notes 额外说明:</strong></p><ul><li>Higher specifications are recommended for production use. This minimal setup is only sufficient to run 4 small VMs in a test environment.<br> 实际使用建议更高配置，测试环境只够开4台最小的虚拟机。</li><li>The server must support KVM nested virtualization with VM-x/AMD-V/Hyper-V enabled.<br> 服务器需要支持KVM嵌套虚拟化，需要检测项目<code>VM-x/AMD-V/Hyper-V</code>是启用的。</li><li>The installation uses binary files directly, skipping dependency installation.<br> 跳过env检测安装，直接使用二进制文件，无需安装依赖。</li></ul></blockquote><h3 id="environment-testing-环境检测" tabindex="-1">Environment Testing 环境检测 <a class="header-anchor" href="#environment-testing-环境检测" aria-label="Permalink to &quot;Environment Testing 环境检测&quot;">​</a></h3><p>To test if your server supports KVM nested virtualization: (检测服务器是否支持KVM嵌套虚拟化)</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-L</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://cdn.spiritlhl.net/https://raw.githubusercontent.com/oneclickvirt/ecs/master/goecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">goecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">goecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">./goecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install</span></span></code></pre></div><ul><li>For performance testing in English: <code>./goecs -l=en</code> (select option 8)</li><li>性能测试中文版：<code>./goecs</code> (选择选项8)</li></ul><h3 id="hypervisor-installation-steps-计算节点安装步骤" tabindex="-1">Hypervisor Installation Steps 计算节点安装步骤 <a class="header-anchor" href="#hypervisor-installation-steps-计算节点安装步骤" aria-label="Permalink to &quot;Hypervisor Installation Steps 计算节点安装步骤&quot;">​</a></h3><ol><li><p>Download the installation script 下载安装脚本:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-slk</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://raw.githubusercontent.com/oneclickvirt/webvirtcloud/main/scripts/install_hypervisor.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install_hypervisor.sh</span><span style="color:#A6ACCD;"> \\</span></span>
<span class="line"><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install_hypervisor.sh</span></span></code></pre></div></li><li><p>Run the installation (replace with your Controller IP) 执行安装命令（替换 Controller IP）:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">install_hypervisor.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">x.x.x.x</span></span></code></pre></div><blockquote><p>Replace <code>x.x.x.x</code> with your Controller&#39;s actual IP address.<br> 请将 <code>x.x.x.x</code> 替换为你的 Controller 控制端的实际 IP 地址。</p></blockquote></li></ol><h2 id="troubleshooting-问题排查" tabindex="-1">Troubleshooting 问题排查 <a class="header-anchor" href="#troubleshooting-问题排查" aria-label="Permalink to &quot;Troubleshooting 问题排查&quot;">​</a></h2><h3 id="networkmanager-version-issue-networkmanager-版本问题" tabindex="-1">NetworkManager Version Issue NetworkManager 版本问题 <a class="header-anchor" href="#networkmanager-version-issue-networkmanager-版本问题" aria-label="Permalink to &quot;NetworkManager Version Issue NetworkManager 版本问题&quot;">​</a></h3><p>You might see this error during installation: 安装过程中可能会自动退出并提示 NetworkManager 版本问题:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">Connection &#39;eth0&#39; (bed050e9-ff49-4665-8112-24ddf2a1cd3c) successfully deleted.</span></span>
<span class="line"><span style="color:#A6ACCD;">Warning: nmcli (1.48.10) and NetworkManager (1.42.2) versions don&#39;t match. Restarting NetworkManager is advised.</span></span>
<span class="line"><span style="color:#A6ACCD;">Error: Failed to add &#39;br-ext&#39; connection: connection.autoconnect-ports: unknown property</span></span></code></pre></div><p><strong>Solution 解决方法:</strong> Reboot your server, then run the installation command again.<br> 重启服务器后重新执行安装命令。</p><h3 id="installation-time-notes-安装耗时提示" tabindex="-1">Installation Time Notes 安装耗时提示 <a class="header-anchor" href="#installation-time-notes-安装耗时提示" aria-label="Permalink to &quot;Installation Time Notes 安装耗时提示&quot;">​</a></h3><ul><li>The entire process takes approximately <strong>20-25 minutes</strong>.<br> 整个过程大约耗时 <strong>20~25 分钟</strong>。</li><li>Most time is spent downloading <code>finnix-125.iso</code> to <code>/var/lib/libvirt/isos/finnix-125.iso</code>.<br> 大部分时间用于下载 <code>finnix-125.iso</code> 至 <code>/var/lib/libvirt/isos/finnix-125.iso</code>。</li><li>This download cannot be accelerated; use <code>tmux</code> or <code>screen</code> to prevent interruption.<br> 该部分无法加速，<strong>建议使用 tmux 或 screen</strong> 等工具防止中断。</li></ul><h3 id="adding-compute-node-to-controller-panel-添加计算节点到控制面板" tabindex="-1">Adding Compute Node to Controller Panel 添加计算节点到控制面板 <a class="header-anchor" href="#adding-compute-node-to-controller-panel-添加计算节点到控制面板" aria-label="Permalink to &quot;Adding Compute Node to Controller Panel 添加计算节点到控制面板&quot;">​</a></h3><p>After installation, a <strong>Token</strong> will be generated for adding the compute node to the Controller:<br> 安装完成后会生成一个 <strong>Token</strong>，用于在控制端（Admin 面板）中添加计算节点：</p><p>Path 路径: <code>Admin Panel &gt; Computers &gt; Add</code></p><h3 id="troubleshooting-node-connection-issues-纳管节点错误排查" tabindex="-1">Troubleshooting Node Connection Issues 纳管节点错误排查 <a class="header-anchor" href="#troubleshooting-node-connection-issues-纳管节点错误排查" aria-label="Permalink to &quot;Troubleshooting Node Connection Issues 纳管节点错误排查&quot;">​</a></h3><p>From Controller 在控制端执行:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">telnet</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&lt;</span><span style="color:#C3E88D;">node</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">i</span><span style="color:#A6ACCD;">p</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">8884</span></span></code></pre></div><p>From Hypervisor 在Hypervisor端执行:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">journalctl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-xeu</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">webvirtcompute</span></span></code></pre></div><h3 id="restarting-docker-containers-after-controller-reboot-controller重启后docker容器重启" tabindex="-1">Restarting Docker Containers After Controller Reboot Controller重启后Docker容器重启 <a class="header-anchor" href="#restarting-docker-containers-after-controller-reboot-controller重启后docker容器重启" aria-label="Permalink to &quot;Restarting Docker Containers After Controller Reboot Controller重启后Docker容器重启&quot;">​</a></h3><p>If Docker containers don&#39;t auto-restart after a system reboot, run:<br> 执行以下命令重启所有容器:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">docker</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">start</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">$(</span><span style="color:#FFCB6B;">docker</span><span style="color:#C3E88D;"> ps -a -q</span><span style="color:#89DDFF;">)</span></span></code></pre></div><h2 id="credits-鸣谢" tabindex="-1">Credits 鸣谢 <a class="header-anchor" href="#credits-鸣谢" aria-label="Permalink to &quot;Credits 鸣谢&quot;">​</a></h2><p><a href="https://webvirt.cloud/" target="_blank" rel="noreferrer">https://webvirt.cloud/</a></p>`,46),l=[t];function r(i,p,c,d,h,u){return e(),o("div",null,l)}const y=s(n,[["render",r]]);export{m as __pageData,y as default};
